from dash import Dash, Input, Output, ctx, html

from dash_signature import Signature, Test

app = Dash(__name__)
value_store = ""
app.layout = html.Div(
    [
        # Signature(id="input_signature", value="", save=False, resize=False),
        # Test(id="input_test", value=""),
        Signature(
            id="input_signature",
            value="data:image/svg+xml;base64,",
        ),
        html.Button(
            "Clear",
            id="clear-button",
            style={"padding": "10px", "backgroundColor": "red"},
        ),
        html.Button(
            "Save",
            id="save-button",
            style={"padding": "10px", "backgroundColor": "green"},
        ),
        html.Button(
            "Store",
            id="store-button",
            style={"padding": "10px", "backgroundColor": "blue"},
        ),
        html.Button(
            "Restore",
            id="restore-button",
            style={"padding": "10px", "backgroundColor": "yellow"},
        ),
        html.Div(
            id="output",
            style={
                "backgroundColor": "lightgrey",
                "width": "1000px",
            },
        ),
    ],
    id="container",
    style={"width": "600px"},
)


@app.callback(
    Output("input_signature", "save"),
    Input("save-button", "n_clicks"),
)
def save_signature_value(n_clicks):
    if n_clicks is not None and n_clicks > 0:
        return True
    return False


@app.callback(
    Output("input_test", "value"),
    [
        Input("clear-button", "n_clicks"),
    ],
)
def submit_signature_value(clicks):
    if clicks is not None and clicks > 0:
        return ""


@app.callback(
    Output("output", "children"),
    [Input("input_test", "value"), Input("store-button", "n_clicks")],
)
def update_output(value, clicks):
    context = ctx.triggered_id if not None else "no trigger"
    if clicks is not None and clicks > 0 and context == "store-button":
        return value


if __name__ == "__main__":
    app.run_server(debug=True)
